import React, { useState, useEffect } from 'react';
import Header from './components/Header/Header';
import EmployeeList from './components/EmployeeList/EmployeeList';
import EmployeeForm from './components/EmployeeForm/EmployeeForm';
import LoadingSpinner from './components/LoadingSpinner/LoadingSpinner';
import './App.css';

function App() {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const API_BASE_URL = 'http://localhost:8000';

  // Fetch all employees
  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/getAll`);
      const data = await response.json();
      if (response.ok) {
        setEmployees(Array.isArray(data) ? data : []);
      } else {
        console.error('Failed to fetch employees:', data);
        setEmployees([]);
      }
    } catch (error) {
      console.error('Error fetching employees:', error);
      setEmployees([]);
    } finally {
      setLoading(false);
    }
  };

  // Add new employee
  const addEmployee = async (employeeData) => {
    try {
      const response = await fetch(`${API_BASE_URL}/addEmp`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(employeeData),
      });
      const data = await response.json();
      if (response.ok) {
        fetchEmployees();
        setShowForm(false);
        showNotification('Employee added successfully!', 'success');
      } else {
        showNotification(data.message || 'Failed to add employee', 'error');
      }
    } catch (error) {
      console.error('Error adding employee:', error);
      showNotification('Error adding employee', 'error');
    }
  };

  // Update employee
  const updateEmployee = async (employeeId, employeeData) => {
    try {
      const response = await fetch(`${API_BASE_URL}/emp/${employeeId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(employeeData),
      });
      const data = await response.json();
      if (response.ok) {
        fetchEmployees();
        setEditingEmployee(null);
        setShowForm(false);
        showNotification('Employee updated successfully!', 'success');
      } else {
        showNotification(data.error || 'Failed to update employee', 'error');
      }
    } catch (error) {
      console.error('Error updating employee:', error);
      showNotification('Error updating employee', 'error');
    }
  };

  // Delete employee
  const deleteEmployee = async (employeeId) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        const response = await fetch(`${API_BASE_URL}/deleteEmp/${employeeId}`, {
          method: 'DELETE',
        });
        const data = await response.json();
        if (response.ok) {
          fetchEmployees();
          showNotification('Employee deleted successfully!', 'success');
        } else {
          showNotification(data.error || 'Failed to delete employee', 'error');
        }
      } catch (error) {
        console.error('Error deleting employee:', error);
        showNotification('Error deleting employee', 'error');
      }
    }
  };

  const showNotification = (message, type) => {
    // Create notification element
    const notification = document.createElement('div');
    notification.className = `notification ${type}`;
    notification.textContent = message;
    document.body.appendChild(notification);

    // Remove after 3 seconds
    setTimeout(() => {
      document.body.removeChild(notification);
    }, 3000);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setShowForm(true);
  };

  const handleCancelForm = () => {
    setEditingEmployee(null);
    setShowForm(false);
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  return (
    <div className="App">
      <Header />
      <main className="main-content">
        <div className="hero-section">
          <div className="container">
            <div className="hero-content">
              <h1>Employee Management System</h1>
              <p>Manage your team efficiently with our modern and intuitive platform</p>
              <button 
                className="cta-button"
                onClick={() => setShowForm(true)}
              >
                <span className="button-icon">👥</span>
                Add New Employee
              </button>
            </div>
            <div className="hero-stats">
              <div className="stat-card">
                <div className="stat-number">{employees.length}</div>
                <div className="stat-label">Total Employees</div>
              </div>
            </div>
          </div>
        </div>

        <div className="container">
          {showForm && (
            <EmployeeForm
              employee={editingEmployee}
              onSubmit={editingEmployee ? updateEmployee : addEmployee}
              onCancel={handleCancelForm}
            />
          )}

          {loading ? (
            <LoadingSpinner />
          ) : (
            <EmployeeList
              employees={employees}
              onEdit={handleEdit}
              onDelete={deleteEmployee}
            />
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
